<?php

/**
 * Alerte_bpiTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class Alerte_bpiTable extends Doctrine_Table
{
  /**
   * Returns an instance of this class.
   *
   * @return Alerte_bpiTable
   */
  public static function getInstance()
  {
    return Doctrine_Core::getTable('Alerte_bpi');
  }

  /**
   * Permet de recuperer tous les alertes d'un dossier et d'un type
   * @param integer $intDossierBpiId
   * @param integer $intTypeAlerteBpiId ID de type des alertes. Utilise les constantes de la table Type_alerte_bpiTable.
   * @param string $strDateEcheance
   * @param integer $intActionId
   * @param integer $intBrevetId
   * @param boolean $boolDejaPasse
   * @return Doctrine_Collection
   * @author Gabor JAGER
   */
  public function getAlerteByDossierIdTypeAlerte($intDossierBpiId, $intTypeAlerteBpiId, $strDateEcheance, $intActionId = null, $intBrevetId = null, $boolDejaPasse = null)
  {
    $objRequete = $this->createQuery('a')
                       ->where('a.dossier_bpi_id = ?', $intDossierBpiId)
                       ->andWhere('a.type_alerte_bpi_id = ?', $intTypeAlerteBpiId)
                       ->andWhere('a.date_echeance = ?', $strDateEcheance);

    if ($intActionId != null)
    {
      $objRequete->andWhere('a.action_id = ?', $intActionId);
    }

    if ($intBrevetId != null)
    {
      $objRequete->andWhere('a.brevet_id = ?', $intBrevetId);
    }

    if ($boolDejaPasse != null)
    {
      $objRequete->andWhere('a.deja_passe = ?', $boolDejaPasse);
    }

    return $objRequete->execute();
  }
}
