class Brevet extends BaseBrevet {


  public function __toString() {
    return $this->getTitre() ;
  }

  public function getNumeroTitre()  {
    if ($this->getNumeroPublication() != NULL || $this->getTitre() != NULL) {
      if ($this->getNumeroPublication() != NULL && $this->getTitre() != NULL)
        return $this->getNumeroPublication()."-".$this->getTitre();
      return $this->getNumeroPublication().$this->getTitre();
    }
    
    if ($this->getTypeDepotId() != NULL)
      $objTypeDepot = Type_depotTable::getInstance ()->findOneById($this->getTypeDepotId());
      return $objTypeDepot->getIntituleComplet();
    
    return NULL;
  }
}