class Budget_type extends BaseBudget_type
{
  public function  __toString()
  {
    $this->logDebug(" [".__FUNCTION__."] /Ligne: ".__LINE__."/ DEBUT; ");

    $strIntitule = $this->getIntitule();

    if ($this->getEstNegatif())
    {
      $strIntitule .= " (-)";
    } else
    {
      $strIntitule .= " (+)";
    }

    $this->logDebug(" [".__FUNCTION__."] /Ligne: ".__LINE__."/ FIN; ");

    return $strIntitule;
  }

  /**
   * Recupere le nom alternatif (affichage listing) du type
   *
   * @return string
   *
   * @author Simeon PETEV
   */
  public function getNomAlternatif()
  {
    $this->logDebug(" [".__FUNCTION__."] /Ligne: ".__LINE__."/ DEBUT; ");

    $strNomAlter = "";
    
    if ($this->getId() == Budget_typeTable::RESTITUE)
    {
      $strNomAlter = libelle('msg_libelle_restitution');
    } else
    {
      $strNomAlter = libelle('msg_libelle_allocation');
    }

    $this->logDebug(" [".__FUNCTION__."] /Ligne: ".__LINE__."/ FIN; ");

    return $strNomAlter;
  }
}