<?php

/**
 * DepartementTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class DepartementTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object DepartementTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Departement');
    }

    /**
     * Retourne les départements par ordre de code départemental
     *
     * @return DoctrineCollection
     * Auteurs: William Richards
     */
    public function retrieveDepartements($objRequeteDoctrine = null, $filtresActif = false){
      if ($objRequeteDoctrine == null){
        $objRequeteDoctrine = $this->createQuery();
      }
      $rootAlias = $objRequeteDoctrine->getRootAlias();
      if ($filtresActif){
        $objRequeteDoctrine = $objRequeteDoctrine->where($rootAlias.'.est_actif = ?', true);
      }
      return $objRequeteDoctrine->orderBy('code_departemental');
    }
}