<?php

/**
 * Document_mrisTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class Document_mrisTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object Document_mrisTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Document_mris');
    }

     public function retrieveDocumentsByDossier($intDossierId,$strTypeDossier,$boolEstJoint)
    {
      $q = Doctrine_Query::create()
        -> from ('Document_mris d')
        -> where('d.'.$strTypeDossier.'_id = ?',$intDossierId)
        -> andWhere('d.est_joint = ?',$boolEstJoint)
        -> orderBy('created_at');

        return $q ->execute();
    }
}