class Documents_mip extends BaseDocuments_mip
{
  public function  __toString()
  {
    return $this->getTitre();
  }

  public function delete(Doctrine_Connection $conn = null) {
    $utilFichier = new UtilFichier();
    $utilArbo = new ServiceArborescence();
    $strChemin = $utilArbo->getRepertoireDocumentsDossierMIP($this->getDossier_mip()->getNumero());
    $utilFichier->supprimerFichier($strChemin.$this->getFichier());
    parent::delete($conn);
  }

}