<?php

/**
 * Documents_mipTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class Documents_mipTable extends Doctrine_Table
{
  const LETTRE_SOUTIEN = '1';
    /**
     * Returns an instance of this class.
     *
     * @return object Documents_mipTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Documents_mip');
    }



    public function retrieveDocumentsByDossier($intDossierId,$boolEstJoint)
    {
      $q = Doctrine_Query::create()
        -> from ('Documents_mip d')
        -> where('d.dossier_mip_id = ?',$intDossierId)
        -> andWhere('d.est_joint = ?',$boolEstJoint)
        -> orderBy('created_at');

        return $q ->execute();
    }    
}
