<?php

/**
 * Evenement_mipTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class Evenement_mipTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object Evenement_mipTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Evenement_mip');
    }

    /**
     * Retourne les évènements d'un dossier MIP orderBy date
     *
     * @param Int $dossierId Id du dossier MIP
     * @return Doctrine_collection
     * @author Actimage
     */
    public function retrieveEvenementOrderByDate($dossierId){

      $doctrineQuery = $this->createQuery('e')
                        ->where('e.dossier_mip_id = ?',$dossierId )
                        ->orderBy('date ASC')
                        ->execute();

      return $doctrineQuery ;

    }
}