class Evenement_mris extends BaseEvenement_mris
{
  public function getMoisByNumero($strNumero)
  {
    $strIndice = str_replace('0', '', $strNumero);
    
    $arrMois = explode(",", str_replace(' ', '', sfConfig::get("app_mois")));
    
    return $arrMois[$strIndice-1];
    
  }

}