<?php

/**
 * FinancementTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class FinancementTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object FinancementTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Financement');
    }

  /**
   * Creer un query permettant de recupere les financements par ordre decendant
   * de la date
   *
   * @param $intDossierId - Numero de dossier mip
   * @return Doctrine_Query
   *
   * @author Simeon PETEV
   */
  public function buildQueryFinancementsOrdreDescDate($intDossierId=0)
  {
    if (sfContext::hasInstance())
        sfContext::getInstance()->getLogger()->debug("{".__CLASS__."} [".__FUNCTION__."] /Ligne: ".__LINE__."/ DEBUT; ");

    $objQuery = $this->getInstance()->getQueryObject()
                                        ->where('dossier_mip_id = ?',$intDossierId)
                                        ->orderBy('date_financement DESC')
    ;

    if (sfContext::hasInstance())
        sfContext::getInstance()->getLogger()->debug("{".__CLASS__."} [".__FUNCTION__."] /Ligne: ".__LINE__."/ FIN; ");

    return $objQuery;
  }

  /**
   * Recupere les financements par ordre decendant de la date
   *
   * @return Doctrine_Collection
   *
   * @author Simeon PETEV
   */
  public function retreveFinancementsOrdreDescDate($intDossierId=0)
  {
    if (sfContext::hasInstance())
        sfContext::getInstance()->getLogger()->debug("{".__CLASS__."} [".__FUNCTION__."] /Ligne: ".__LINE__."/ DEBUT; ");

    $arrFinancements = $this->buildQueryFinancementsOrdreDescDate($intDossierId)->execute();

    if (sfContext::hasInstance())
        sfContext::getInstance()->getLogger()->debug("{".__CLASS__."} [".__FUNCTION__."] /Ligne: ".__LINE__."/ FIN; ");

    return $arrFinancements;
  }

  /**
   * Recupere les anness dans lesquel il y des finnacements
   *
   * @return array Avec des annes
   *
   * @author Simeon PETEV
   */
  public function getAnneesFinancementsOrdreDesc()
  {
    if (sfContext::hasInstance())
        sfContext::getInstance()->getLogger()->debug("{".__CLASS__."} [".__FUNCTION__."] /Ligne: ".__LINE__."/ DEBUT; ");

    $objDataBaseHandler = $this->getConnection()->getDbh();

    $strQuery = "SELECT DISTINCT YEAR(date_financement) FROM financement ORDER BY date_financement DESC";

    $objQuery = $objDataBaseHandler->query($strQuery);

    $arrAnneesBrutes = $objQuery->fetchAll();

    $arrAnnees = array();

    foreach ($arrAnneesBrutes as $key => $value)
    {
      $arrAnnees[$value[0]] = $value[0];
    }

    if (sfContext::hasInstance())
        sfContext::getInstance()->getLogger()->debug("{".__CLASS__."} [".__FUNCTION__."] /Ligne: ".__LINE__."/ FIN; ");

    return $arrAnnees;
  }

  /**
   * Recupere le query permettant de recupere les financements (tout dossier confondu)
   * pour une année donnée
   *
   * @param string $strAnnee
   * @return Doctrine_Query
   *
   * @author Simeon PETEV
   */
  public function buildQueryFinancementsPourAnnee($strAnnee)
  {
    if (sfContext::hasInstance())
        sfContext::getInstance()->getLogger()->debug("{".__CLASS__."} [".__FUNCTION__."] /Ligne: ".__LINE__."/ DEBUT; ");

    $objQuery = $this->getInstance()->getQueryObject()
                                        ->where("YEAR(date_financement) = ?",$strAnnee)
    ;

    if (sfContext::hasInstance())
        sfContext::getInstance()->getLogger()->debug("{".__CLASS__."} [".__FUNCTION__."] /Ligne: ".__LINE__."/ FIN; ");

    return $objQuery;
  }

  /**
   * Recupere les financements (tout dossier confondu) pour une année donnée
   *
   * @param string $strAnnee
   * @return Doctrine_Collection
   *
   * @author Simeon PETEV
   */
  public function retreveFinancementsPourAnnee($strAnnee)
  {
    if (sfContext::hasInstance())
        sfContext::getInstance()->getLogger()->debug("{".__CLASS__."} [".__FUNCTION__."] /Ligne: ".__LINE__."/ DEBUT; ");

    $arrResultats = $this->buildQueryFinancementsPourAnnee($strAnnee)->execute();

    if (sfContext::hasInstance())
        sfContext::getInstance()->getLogger()->debug("{".__CLASS__."} [".__FUNCTION__."] /Ligne: ".__LINE__."/ FIN; ");

    return $arrResultats;
  }
}