    /**
     *  Retrouve un libelle en fonction d'un organisme MINDEF et d'un metier
     * @param int $organismeId Id de l'organisme MINDEF
     * @param int $metierId Id du metier de l'utilisateur
     * @return Doctrine_Query
     * @author Alexandre WETTA
     */
    public function retrieveLibelleByMetier($organismeId, $metierId) {

    $query = $this->createQuery("l")
                    ->where('organisme_mindef_id = ?', $organismeId)
                    ->andWhere('metier_id = ?', $metierId);

    return $query;
  }
  
    /**
     *  Retrouve un libelle en fonction d'un organisme MINDEF et d'un metier
     * @param int $organismeId Id de l'organisme MINDEF
     * @param int $metierId Id du metier de l'utilisateur
     * @return Doctrine_Query
     * @author Alexandre WETTA
     */
    public function getLibelleByMetier($organismeId, $metierId) {

    $query = $this->createQuery("l")
                    ->where('organisme_mindef_id = ?', $organismeId)
                    ->andWhere('metier_id = ?', $metierId);
	$arrLibelles = $query->execute();
	
    return $arrLibelles[0];
  }
