 */
class PaysTable extends Doctrine_Table
{

  const FRANCE = "78";
  
    /**
     * Returns an instance of this class.
     *
     * @return object PaysTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Pays');
    }

  /**
   * Construit le query permettant de recuperer tous les pays triÃ©s par ordre
   * ascendant de leur nom
   *
   * @return Doctrine_Query
   *
   * @author Simeon PETEV
   */
  public function buildQueryPaysOrdreAscNom()
  {
    if (sfContext::hasInstance())
      sfContext::getInstance()->getLogger()->debug("{".__CLASS__."} [".__FUNCTION__."] /Ligne: ".__LINE__."/ DEBUT; ");

    $objQuery = $this->getInstance()->getQueryObject()
                                        ->orderBy('nom ASC')
    ;

    if (sfContext::hasInstance())
      sfContext::getInstance()->getLogger()->debug("{".__CLASS__."} [".__FUNCTION__."] /Ligne: ".__LINE__."/ FIN; ");

    return $objQuery;
  }
}