<?php

/**
 * Utilisateur_profilTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class Utilisateur_profilTable extends Doctrine_Table
{

    /**
     * Returns an instance of this class.
     *
     * @return object Utilisateur_profilTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Utilisateur_profil');
    }

    /**
     * Recupere un array de identifiant de utilisateurs ayant un profil donnÃ©
     *
     * @param integer $idProfil
     * @return array Un array de identifiants d'e Profils'Utilisateurs
     *
     * @author Simeon PETEV
     */
    public function getUtilisateursIdsByProfilId($idProfil)
    {
      if (sfContext::hasInstance())
        sfContext::getInstance()->getLogger()->debug("{".__CLASS__."} [".__FUNCTION__."] /Ligne: ".__LINE__."/ DEBUT; ");

      $arrProfilsIds = array();

      $objQuery = Utilisateur_profilTable::getInstance()->getQueryObject()
                    ->where('profil_id'." = ?",$idProfil);

      $arrUtilisateursProfils = $objQuery->execute();

      foreach ($arrUtilisateursProfils as $objUtilProfil)
      {
        $arrProfilsIds[] = $objUtilProfil->getUtilisateurId();
      }

      if (sfContext::hasInstance())
        sfContext::getInstance()->getLogger()->debug("{".__CLASS__."} [".__FUNCTION__."] /Ligne: ".__LINE__."/ FIN; ");

      return $arrProfilsIds;
    }
}