<?php

/**
 * Valorisation_externeTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class Valorisation_externeTable extends Doctrine_Table
{
  /**
   * Returns an instance of this class.
   *
   * @return object Valorisation_externeTable
   */
  public static function getInstance()
  {
    return Doctrine_Core::getTable('Valorisation_externe');
  }

  /**
   * Permet de recuperer tous les valorisations externes d'un dossier
   * @param integer $intDossierId
   * @return Doctrine_Collection
   * @author Gabor JAGER
   */
  public function getValorisationsExternesByDossierId($intDossierId)
  {
    $objRequete = $this->createQuery('ve')
                       ->leftJoin("ve.Valorisation_bpi vb ON ve.valorisation_bpi_id = vb.id")
                       ->where("vb.dossier_bpi_id = ?", $intDossierId);

    return $objRequete->execute();
  }
}